function divideLaranjas(){
    var laranjas = document.getElementById("numeroLaranjas").value;
    var vizinhos = document.getElementById("numeroVizinhos").value;

    var laranjaVizinho = document.querySelector("#laranjaVizinho");
    var sobraJoselito = document.querySelector("#sobraJoselito");

    var divisao = Math.trunc(laranjas/vizinhos);
    var resto = laranjas%vizinhos;



    if (laranjas < 0){
        laranjaVizinho.innerHTML = '<h1>Você é idiota? Laranjas negativas?</h1>';
        sobraJoselito.innerHTML = '<img src="data:image/jpeg;base64,">';
    } else if (vizinhos < 0){
        laranjaVizinho.innerHTML = '<h1>Você é idiota? Vizinhos negativos?</h1>';  
        sobraJoselito.innerHTML = '<img src="data:image/jpeg;base64,">';
    } else if (laranjas == 0){
        laranjaVizinho.innerHTML = '<h1>Você não tem laranjas, como você quer dividir?';  
        sobraJoselito.innerHTML = '<img src="data:image/jpeg;base64,">';
    } else if (vizinhos == 0){
        laranjaVizinho.innerHTML = '<h2>Você não tem amigos? Coma as ' + laranjas + ' laranjas sozinho!</h2>';
        sobraJoselito.innerHTML = '';
    } else if (divisao == 0){
        laranjaVizinho.innerHTML = '<h1>Me parece que seus vizinhos ficarão com fome, ninguém vai ganhar nada</h1><img src="data:image/png;base64,">' ;
    } else{
        laranjaVizinho.innerHTML = '<h1>Tomem ' + divisao + ' laranjas</h1><img src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRPEs9_M5tOyqnCNdzD22KdF6Vsf7PED1ZKdc9UPRk3rONzAP_5&usqp=CAU\\">';

    }


    if (resto == 0 && vizinhos > 0 && laranjas > 0){
        sobraJoselito.innerHTML = '<h2>Não sobrou nada</h2><img scr="data:image/jpeg;base64,">';
    } else if (resto != 0 && vizinhos > 0 && laranjas > 0){
        sobraJoselito.innerHTML = '<h2>Sobrará ' + resto + ' laranjas a mais para Joselito</h2>';
    }








}